import { useState } from "react";
import Item from "./Item"; // Add this line to import the Item component

const ItemList = () => {

    const itemDataArray = [
        {
            name: "CPU",
            price: 310000,
            quantity: 1,
        },
        {
            name: "RAM",
            price: 80000,
            quantity: 2,
        },
        {
            name: "SSD",
            price: 120000,
            quantity: 1,
        },
    ];

    const [items] = useState(itemDataArray);

    return (
    <div>
        <h2>상품 목록</h2>
        <ul>
            {items.map((item, index) => (
                <Item key={index} item={item} />
            ))}
        </ul>
    </div>
    );
};

export default ItemList;
