import { useState } from "react";
import styles from './NoticeForm.module.css';

const render = ({ title, content, date }) => {
    return (
        <div className={styles.noticeContainer}>
            <table className={styles.noticeTable}>
                <tbody>
                    <tr>
                        <th>제목</th>
                        <td>{title}</td>
                    </tr>
                    <tr>
                        <th>내용</th>
                        <td>{content}</td>
                    </tr>
                    <tr>
                        <th>날짜</th>
                        <td>{date}</td>
                    </tr>
                </tbody>
            </table>
        </div>
    );
};

const NoticeForm = () => {

    return (
        <div className={styles.noticeForm}>

            <div className={styles.inputContainer}>
                <input
                    name="title"
                    value={form.title}
                    onChange={handleOnChange}
                    onKeyPress={onKeyPress}
                    placeholder="제목"
                    className={styles.inputField}
                />
                <textarea
                    name="content"
                    value={form.content}
                    onChange={handleOnChange}
                    onKeyPress={onKeyPress}
                    placeholder="내용"
                    className={styles.textareaField}
                />
                <input
                    name="date"
                    type="date"
                    value={form.date}
                    onChange={handleOnChange}
                    onKeyPress={onKeyPress}
                    className={styles.inputField}
                />
                <button onClick={handleOnClick} className={styles.submitButton}>제출</button>
            </div>
            
            {isModalOpen && selectedNotice && (
                <>
                    <div className={styles.overlay} onClick={closeModal}></div>
                    <div className={styles.modal}>
                        {render(selectedNotice)}
                        <button onClick={closeModal} className={styles.submitButton}>닫기</button>
                    </div>
                </>
            )}
        </div>
    );
};

export default NoticeForm;
