import { useState } from "react";
import './NoticeForm.css';

const render = (form) => {
    const { title, content, date } = form;
    return (
        <div className="notice-container">
            <h2>공지사항</h2>
            <table className="notice-table">
                <tbody>
                    <tr>
                        <th>제목</th>
                        <td>{title}</td>
                    </tr>
                    <tr>
                        <th>내용</th>
                        <td>{content}</td>
                    </tr>
                    <tr>
                        <th>날짜</th>
                        <td>{date}</td>
                    </tr>
                </tbody>
            </table>
        </div>
    );
};

const NoticeForm = () => {
    
    const [form, setForm] = useState({
        title: '',
        content: '',
        date: ''
    });
    const [output, setOutput] = useState(null);

    const {title, content, date} = form;
    const handleOnChange = e => {
        const nextForm = {
            ...form,
            [e.target.name]: e.target.value
        };
        setForm(nextForm);
    };

    const onKeyDown = e => {
        if (e.key === 'Enter') {
            handleOnClick();
        }
    };
    const handleOnClick = () => {
        if(title.trim() === ""){
            alert("제목을 입력해주세요.");
            return;
        }
        if(content.trim() === ""){
            alert("내용을 입력해주세요.");
            return;
        }
        setOutput(render(form));
    };

    return (
        <div className="notice-form">
            <div className="input-container">
                <input
                    name="title"
                    value={form.title}
                    onChange={handleOnChange}
                    onKeyDown={onKeyDown}
                    placeholder="제목"
                    className="input-field"
                />
                <textarea
                    name="content"
                    value={form.content}
                    onChange={handleOnChange}
                    onKeyDown={onKeyDown}
                    placeholder="내용"
                    className="textarea-field"
                />
                <input
                    name="date"
                    type="date"
                    value={form.date}
                    onChange={handleOnChange}
                    onKeyDown={onKeyDown}
                    className="input-field"
                />
                <button onClick={handleOnClick} className="submit-button">제출</button>
            </div>
            {output}
        </div>
    );
};

export default NoticeForm;
